    /// but all coordinates are rotated 270° around the center of the boundary.
    /// </summary>
    [Test]
    public void FivePointsInAForkedTallCross_Rotated270_CenteredAtOrigin()
    {
        // Arrange

        VoronoiPlane plane = new VoronoiPlane(-500, -500, 500, 500);
        List<VoronoiSite> sites =
        [
            new VoronoiSite(-400, 0), // #1
            new VoronoiSite(-200, -200), // #2
            new VoronoiSite(400, -200), // #3
            new VoronoiSite(400, 200), // #4
            new VoronoiSite(-200, 200), // #5
        ];
        plane.SetSites(sites);

        //  500 Z-----------------------------F-------------------Y
        //      |                             |                   |
        //  400 |                             |                   |
        //      |                             |                   |
        //  300 D,                            |                   |
        //      | ',                          |                   |
        //  200 |   '·,        5              |              4    |
        //      |      ',                     |                   |
        //  100 |        '·,                  |                   |
        //      |           ',                |                   |
        //    0 |    1        #B---------●----A-------------------G
        //      |           ,'                |                   |
        // -100 |        ,·'                  |                   |
        //      |      ,'                     |                   |
        // -200 |   ,·'        2              |              3    |
        //      | ,'                          |                   |
        // -300 C'                            |                   |
        //      |                             |                   |
        // -400 |                             |                   |
        //      |                             |                   |
        // -500 W-----------------------------E-------------------X
        //    -500 -400 -300 -200 -100    0  100  200  300  400  500 

        // Act

        plane.Tessellate();
        List<VoronoiEdge> edges = plane.Edges;

        // Assume

        Assume.That(edges.Count, Is.EqualTo(15), "Expected: edge count 15");
        Assume.That(edges, Is.Not.Null);
        Assume.That(HasEdge(edges, 100, 0, -200, 0), Is.True, "Expected: has edge A-B"); // A-B
        Assume.That(HasEdge(edges, -200, 0, -500, -300), Is.True, "Expected: has edge B-C"); // B-C
        Assume.That(HasEdge(edges, -200, 0, -500, 300), Is.True, "Expected: has edge B-D"); // B-D
        Assume.That(HasEdge(edges, 100, 0, 100, -500), Is.True, "Expected: has edge A-E"); // A-E
        Assume.That(HasEdge(edges, 100, 0, 100, 500), Is.True, "Expected: has edge A-F"); // A-F
        Assume.That(HasEdge(edges, 100, 0, 500, 0), Is.True, "Expected: has edge A-G"); // A-G
        Assume.That(HasEdge(edges, -500, -300, -500, -500), Is.True, "Expected: has edge C-W"); // C-W
        Assume.That(HasEdge(edges, -500, -500, 100, -500), Is.True, "Expected: has edge W-E"); // W-E
        Assume.That(HasEdge(edges, 100, -500, 500, -500), Is.True, "Expected: has edge E-X"); // E-X
        Assume.That(HasEdge(edges, 500, -500, 500, 0), Is.True, "Expected: has edge X-G"); // X-G
        Assume.That(HasEdge(edges, 500, 0, 500, 500), Is.True, "Expected: has edge G-Y"); // G-Y
        Assume.That(HasEdge(edges, 500, 500, 100, 500), Is.True, "Expected: has edge Y-F"); // Y-F
        Assume.That(HasEdge(edges, 100, 500, -500, 500), Is.True, "Expected: has edge F-Z"); // F-Z
        Assume.That(HasEdge(edges, -500, 500, -500, 300), Is.True, "Expected: has edge Z-D"); // Z-D
        Assume.That(HasEdge(edges, -500, 300, -500, -300), Is.True, "Expected: has edge D-C"); // D-C

        // Assert

        Assert.That(sites[0].Cell, Is.Not.Null);
        Assert.That(sites[0].Cell.Count(), Is.EqualTo(3)); // #1
        Assert.That(HasEdge(sites[0].Cell, -200, 0, -500, -300), Is.True); // #1 has B-C
        Assert.That(HasEdge(sites[0].Cell, -200, 0, -500, 300), Is.True); // #1 has B-D
        Assert.That(HasEdge(sites[0].Cell, -500, 300, -500, -300), Is.True); // #1 has D-C
        Assert.That(sites[1].Cell, Is.Not.Null);
        Assert.That(sites[1].Cell.Count(), Is.EqualTo(5)); // #2
        Assert.That(HasEdge(sites[1].Cell, 100, 0, -200, 0), Is.True); // #2 has A-B
        Assert.That(HasEdge(sites[1].Cell, -200, 0, -500, -300), Is.True); // #2 has B-C
        Assert.That(HasEdge(sites[1].Cell, 100, 0, 100, -500), Is.True); // #2 has A-E
        Assert.That(HasEdge(sites[1].Cell, -500, -300, -500, -500), Is.True); // #2 has C-W
        Assert.That(HasEdge(sites[1].Cell, -500, -500, 100, -500), Is.True); // #2 has W-E
        Assert.That(sites[2].Cell, Is.Not.Null);
        Assert.That(sites[2].Cell.Count(), Is.EqualTo(4)); // #3
        Assert.That(HasEdge(sites[2].Cell, 100, 0, 100, -500), Is.True); // #3 has A-E
        Assert.That(HasEdge(sites[2].Cell, 100, 0, 500, 0), Is.True); // #3 has A-G
        Assert.That(HasEdge(sites[2].Cell, 100, -500, 500, -500), Is.True); // #3 has E-X
        Assert.That(HasEdge(sites[2].Cell, 500, -500, 500, 0), Is.True); // #3 has X-G
        Assert.That(sites[3].Cell, Is.Not.Null);
        Assert.That(sites[3].Cell.Count(), Is.EqualTo(4)); // #4
        Assert.That(HasEdge(sites[3].Cell, 100, 0, 100, 500), Is.True); // #4 has A-F
        Assert.That(HasEdge(sites[3].Cell, 100, 0, 500, 0), Is.True); // #4 has A-G
        Assert.That(HasEdge(sites[3].Cell, 500, 0, 500, 500), Is.True); // #4 has G-Y
        Assert.That(HasEdge(sites[3].Cell, 500, 500, 100, 500), Is.True); // #4 has Y-F
        Assert.That(sites[4].Cell, Is.Not.Null);
        Assert.That(sites[4].Cell.Count(), Is.EqualTo(5)); // #5
        Assert.That(HasEdge(sites[4].Cell, 100, 0, -200, 0), Is.True); // #5 has A-B
        Assert.That(HasEdge(sites[4].Cell, -200, 0, -500, 300), Is.True); // #5 has B-D
        Assert.That(HasEdge(sites[4].Cell, 100, 0, 100, 500), Is.True); // #5 has A-F
        Assert.That(HasEdge(sites[4].Cell, 100, 500, -500, 500), Is.True); // #5 has F-Z
        Assert.That(HasEdge(sites[4].Cell, -500, 500, -500, 300), Is.True); // #5 has Z-D
    }

    /// <summary>
    /// This test basically repeats <see cref="FivePointsInAForkedTallCross_Rotated270"/> above,
    /// but all coordinates are rotated 270° around the center of the boundary.
    /// </summary>
    [Test]
    public void FivePointsInAForkedTallCross_Rotated270_ShiftedTowardsOrigin()
    {
        // Arrange

        VoronoiPlane plane = new VoronoiPlane(-200, -200, 800, 800);
        List<VoronoiSite> sites =
        [
            new VoronoiSite(-100, 300), // #1
            new VoronoiSite(100, 100), // #2
            new VoronoiSite(700, 100), // #3
            new VoronoiSite(700, 500), // #4
            new VoronoiSite(100, 500), // #5
        ];
        plane.SetSites(sites);

        //  800 Z-----------------------------F-------------------Y
        //      |                             |                   |
        //  700 |                             |                   |
        //      |                             |                   |
        //  600 D,                            |                   |
        //      | ',                          |                   |
        //  500 |   '·,        5              |              4    |
        //      |      ',                     |                   |
        //  400 |        '·,                  |                   |
        //      |           ',                |                   |
        //  300 |    1        #B--------------A-------------------G
        //      |           ,'                |                   |
        //  200 |        ,·'                  |                   |
        //      |      ,'                     |                   |
        //  100 |   ,·'        2              |              3    |
        //      | ,'                          |                   |
        //    0 C'        ●                   |                   |
        //      |                             |                   |
        // -100 |                             |                   |
        //      |                             |                   |
        // -200 W-----------------------------E-------------------X
        //    -200 -100    0  100  200  300  400  500  600  700  800 

        // Act

        plane.Tessellate();
        List<VoronoiEdge> edges = plane.Edges;

        // Assume

        Assume.That(edges.Count, Is.EqualTo(15), "Expected: edge count 15");
        Assume.That(edges, Is.Not.Null);
        Assume.That(HasEdge(edges, 400, 300, 100, 300), Is.True, "Expected: has edge A-B"); // A-B
        Assume.That(HasEdge(edges, 100, 300, -200, 0), Is.True, "Expected: has edge B-C"); // B-C
        Assume.That(HasEdge(edges, 100, 300, -200, 600), Is.True, "Expected: has edge B-D"); // B-D
        Assume.That(HasEdge(edges, 400, 300, 400, -200), Is.True, "Expected: has edge A-E"); // A-E
        Assume.That(HasEdge(edges, 400, 300, 400, 800), Is.True, "Expected: has edge A-F"); // A-F
        Assume.That(HasEdge(edges, 400, 300, 800, 300), Is.True, "Expected: has edge A-G"); // A-G
        Assume.That(HasEdge(edges, -200, 0, -200, -200), Is.True, "Expected: has edge C-W"); // C-W
        Assume.That(HasEdge(edges, -200, -200, 400, -200), Is.True, "Expected: has edge W-E"); // W-E
        Assume.That(HasEdge(edges, 400, -200, 800, -200), Is.True, "Expected: has edge E-X"); // E-X
        Assume.That(HasEdge(edges, 800, -200, 800, 300), Is.True, "Expected: has edge X-G"); // X-G
        Assume.That(HasEdge(edges, 800, 300, 800, 800), Is.True, "Expected: has edge G-Y"); // G-Y
        Assume.That(HasEdge(edges, 800, 800, 400, 800), Is.True, "Expected: has edge Y-F"); // Y-F
        Assume.That(HasEdge(edges, 400, 800, -200, 800), Is.True, "Expected: has edge F-Z"); // F-Z
        Assume.That(HasEdge(edges, -200, 800, -200, 600), Is.True, "Expected: has edge Z-D"); // Z-D
        Assume.That(HasEdge(edges, -200, 600, -200, 0), Is.True, "Expected: has edge D-C"); // D-C

        // Assert

        Assert.That(sites[0].Cell, Is.Not.Null);
        Assert.That(sites[0].Cell.Count(), Is.EqualTo(3)); // #1
        Assert.That(HasEdge(sites[0].Cell, 100, 300, -200, 0), Is.True); // #1 has B-C
        Assert.That(HasEdge(sites[0].Cell, 100, 300, -200, 600), Is.True); // #1 has B-D
        Assert.That(HasEdge(sites[0].Cell, -200, 600, -200, 0), Is.True); // #1 has D-C
        Assert.That(sites[1].Cell, Is.Not.Null);
        Assert.That(sites[1].Cell.Count(), Is.EqualTo(5)); // #2
        Assert.That(HasEdge(sites[1].Cell, 400, 300, 100, 300), Is.True); // #2 has A-B
        Assert.That(HasEdge(sites[1].Cell, 100, 300, -200, 0), Is.True); // #2 has B-C
        Assert.That(HasEdge(sites[1].Cell, 400, 300, 400, -200), Is.True); // #2 has A-E
        Assert.That(HasEdge(sites[1].Cell, -200, 0, -200, -200), Is.True); // #2 has C-W
        Assert.That(HasEdge(sites[1].Cell, -200, -200, 400, -200), Is.True); // #2 has W-E
        Assert.That(sites[2].Cell, Is.Not.Null);
        Assert.That(sites[2].Cell.Count(), Is.EqualTo(4)); // #3
        Assert.That(HasEdge(sites[2].Cell, 400, 300, 400, -200), Is.True); // #3 has A-E
        Assert.That(HasEdge(sites[2].Cell, 400, 300, 800, 300), Is.True); // #3 has A-G
        Assert.That(HasEdge(sites[2].Cell, 400, -200, 800, -200), Is.True); // #3 has E-X
        Assert.That(HasEdge(sites[2].Cell, 800, -200, 800, 300), Is.True); // #3 has X-G
        Assert.That(sites[3].Cell, Is.Not.Null);
        Assert.That(sites[3].Cell.Count(), Is.EqualTo(4)); // #4
        Assert.That(HasEdge(sites[3].Cell, 400, 300, 400, 800), Is.True); // #4 has A-F
        Assert.That(HasEdge(sites[3].Cell, 400, 300, 800, 300), Is.True); // #4 has A-G
        Assert.That(HasEdge(sites[3].Cell, 800, 300, 800, 800), Is.True); // #4 has G-Y
        Assert.That(HasEdge(sites[3].Cell, 800, 800, 400, 800), Is.True); // #4 has Y-F
        Assert.That(sites[4].Cell, Is.Not.Null);
        Assert.That(sites[4].Cell.Count(), Is.EqualTo(5)); // #5
        Assert.That(HasEdge(sites[4].Cell, 400, 300, 100, 300), Is.True); // #5 has A-B
        Assert.That(HasEdge(sites[4].Cell, 100, 300, -200, 600), Is.True); // #5 has B-D
        Assert.That(HasEdge(sites[4].Cell, 400, 300, 400, 800), Is.True); // #5 has A-F
        Assert.That(HasEdge(sites[4].Cell, 400, 800, -200, 800), Is.True); // #5 has F-Z
        Assert.That(HasEdge(sites[4].Cell, -200, 800, -200, 600), Is.True); // #5 has Z-D
    }

