    /// but all coordinates are rotated 90° around the center of the boundary.
    /// </summary>
    [Test]
    public void ThreePointsMeetingSharplyPastBorderPerpendicularly_Rotated90_ShiftedTowardsOrigin()
    {
        // Arrange

        VoronoiPlane plane = new VoronoiPlane(-200, -200, 1000, 1000);
        List<VoronoiSite> sites =
        [
            new VoronoiSite(400, 800), // #1
            new VoronoiSite(700, 900), // #2
            new VoronoiSite(100, 900), // #3
        ];
        plane.SetSites(sites);

        // 1000 Y------------------------D---------A------------------------X
        //      |                       ·           ·                       |
        //  900 |              3       ·             ·       2              |
        //      |                      ·              ·                     |
        //  800 |                     ·       1       ·                     |
        //      |                    ·                 ·                    |
        //  700 |                   ·                   ·                   |
        //      |                  ·                     ·                  |
        //  600 |                 ·                       ·                 |
        //      |                 ·                        ·                |
        //  500 |                ·                         ·                |
        //      |               ·                           ·               |
        //  400 |              ·                             ·              |
        //      |             ·                               ·             |
        //  300 |            ·                                 ·            |
        //      |            ·                                  ·           |
        //  200 |           ·                                   ·           |
        //      |          ·                                     ·          |
        //  100 |         ·                                       ·         |
        //      |        ·                                         ·        |
        //    0 |       · ●                                         ·       |
        //      |       ·                                            ·      |
        // -100 |      ·                                             ·      |
        //      |     ·                                               ·     |
        // -200 W----C-------------------------------------------------B----Z
        //    -200 -100    0  100  200  300  400  500  600  700  800  900 1000 

        // Act

        plane.Tessellate();
        List<VoronoiEdge> edges = plane.Edges;

        // Assume

        Assume.That(edges.Count, Is.EqualTo(10), "Expected: edge count 10");
        Assume.That(edges, Is.Not.Null);
        Assume.That(HasEdge(edges, 500, 1000, 900, -200), Is.True, "Expected: has edge A-B"); // A-B
        Assume.That(HasEdge(edges, -100, -200, 300, 1000), Is.True, "Expected: has edge C-D"); // C-D
        Assume.That(HasEdge(edges, -100, -200, 900, -200), Is.True, "Expected: has edge C-B"); // C-B
        Assume.That(HasEdge(edges, 900, -200, 1000, -200), Is.True, "Expected: has edge B-Z"); // B-Z
        Assume.That(HasEdge(edges, 1000, -200, 1000, 1000), Is.True, "Expected: has edge Z-X"); // Z-X
        Assume.That(HasEdge(edges, 1000, 1000, 500, 1000), Is.True, "Expected: has edge X-A"); // X-A
        Assume.That(HasEdge(edges, 500, 1000, 300, 1000), Is.True, "Expected: has edge A-D"); // A-D
        Assume.That(HasEdge(edges, 300, 1000, -200, 1000), Is.True, "Expected: has edge D-Y"); // D-Y
        Assume.That(HasEdge(edges, -200, 1000, -200, -200), Is.True, "Expected: has edge Y-W"); // Y-W
        Assume.That(HasEdge(edges, -200, -200, -100, -200), Is.True, "Expected: has edge W-C"); // W-C

        // Assert

        Assert.That(sites[0].Cell, Is.Not.Null);
        Assert.That(sites[0].Cell.Count(), Is.EqualTo(4)); // #1
        Assert.That(HasEdge(sites[0].Cell, 500, 1000, 900, -200), Is.True); // #1 has A-B
        Assert.That(HasEdge(sites[0].Cell, -100, -200, 300, 1000), Is.True); // #1 has C-D
        Assert.That(HasEdge(sites[0].Cell, -100, -200, 900, -200), Is.True); // #1 has C-B
        Assert.That(HasEdge(sites[0].Cell, 500, 1000, 300, 1000), Is.True); // #1 has A-D
        Assert.That(sites[1].Cell, Is.Not.Null);
        Assert.That(sites[1].Cell.Count(), Is.EqualTo(4)); // #2
        Assert.That(HasEdge(sites[1].Cell, 500, 1000, 900, -200), Is.True); // #2 has A-B
        Assert.That(HasEdge(sites[1].Cell, 900, -200, 1000, -200), Is.True); // #2 has B-Z
        Assert.That(HasEdge(sites[1].Cell, 1000, -200, 1000, 1000), Is.True); // #2 has Z-X
        Assert.That(HasEdge(sites[1].Cell, 1000, 1000, 500, 1000), Is.True); // #2 has X-A
        Assert.That(sites[2].Cell, Is.Not.Null);
        Assert.That(sites[2].Cell.Count(), Is.EqualTo(4)); // #3
        Assert.That(HasEdge(sites[2].Cell, -100, -200, 300, 1000), Is.True); // #3 has C-D
        Assert.That(HasEdge(sites[2].Cell, 300, 1000, -200, 1000), Is.True); // #3 has D-Y
        Assert.That(HasEdge(sites[2].Cell, -200, 1000, -200, -200), Is.True); // #3 has Y-W
        Assert.That(HasEdge(sites[2].Cell, -200, -200, -100, -200), Is.True); // #3 has W-C
    }

